delimiter $$CREATE TABLE `content` (  `content_id` int(11) NOT NULL,  `url` varchar(2000) NOT NULL,  PRIMARY KEY (`content_id`),  UNIQUE KEY `content_id_UNIQUE` (`content_id`)) ENGINE=InnoDB DEFAULT CHARSET=latin1$$delimiter $$CREATE TABLE `user` (  `user_id` int(11) NOT NULL AUTO_INCREMENT,  `email` varchar(256) DEFAULT NULL,  `password` varchar(50) NOT NULL,  `enabled` tinyint(4) NOT NULL,  PRIMARY KEY (`user_id`),  UNIQUE KEY `user_id_UNIQUE` (`user_id`),  UNIQUE KEY `email_UNIQUE` (`email`)) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=latin1$$delimiter $$CREATE TABLE `user_content` (  `user_id` int(11) NOT NULL,  `content_id` int(11) NOT NULL,  KEY `user_id` (`user_id`),  KEY `content_id` (`content_id`),  CONSTRAINT `content_id` FOREIGN KEY (`content_id`) REFERENCES `content` (`content_id`) ON DELETE NO ACTION ON UPDATE NO ACTION,  CONSTRAINT `user_id` FOREIGN KEY (`user_id`) REFERENCES `user` (`user_id`) ON DELETE NO ACTION ON UPDATE NO ACTION) ENGINE=InnoDB DEFAULT CHARSET=latin1$$